var last_time = msg.data.timeSinceChangedMs

function msToTime(duration) {
  var milliseconds = parseInt((duration % 1000) / 100),
    seconds = Math.floor((duration / 1000) % 60),
    minutes = Math.floor((duration / (1000 * 60)) % 60),
    hours = Math.floor((duration / (1000 * 60 * 60)) % 24);

  hours = (hours < 10) ? "0" + hours : hours;
  minutes = (minutes < 10) ? "0" + minutes : minutes;
  seconds = (seconds < 10) ? "0" + seconds : seconds;

  return hours + ":" + minutes + ":" + seconds + "." + milliseconds;
}

var message = msg.data.entity_id + " last changed " +  msToTime(last_time) + " ago"

return { payload: message,topic:msg.topic };

var hoursLeft = msg.data.new_state.attributes.filter_left;
if (hoursLeft <= 10) {
